import React, { useState } from "react";
import "./style.css";
import info from "./DocInfo";
import Card from "./card";
import Navbar from "./Navbar";

const newArray = [
  ...new Set(
    info.map((curEle) => {
      return curEle.category;
    })
  ),
  "All",
];
const ListMain = () => {
  const [infoData, setInfoData] = useState(info);
  const [infoList, setInfoList] = useState(newArray);
  const filterItem = (category) => {
    if (category === "All") {
      setInfoData(info);
      return;
    }
    const updatedList = info.filter((curEle) => {
      return curEle.category === category;
    });
    setInfoData(updatedList);
  };
  return (
    <>
      <nav className="navbar1" >
        <div className="btn-group1">
          {infoList.map((curEle) => {
            return (
              <button
                className="btn-group-item1"
                key={curEle.category}
                onClick={() => filterItem(curEle)}
              >
                {curEle}
              </button>
            );
          })}
        </div>
      </nav>
      <section className="main-card-container1">
        {infoData.map((info) => {
          return <Card info={info} key={info.id} />;
        })}
      </section>
    </>
  );
};

export default ListMain;
